  color: var(--accent-text-color);
}
@media screen and (min-width: 768px) {
  .title {
    font-size: 30px;
  }
}
@media screen and (min-width: 1200px) {
  .title {
    font-size: 50px;
  }
}

.search-form {
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
}
@media screen and (min-width: 768px) {
  .search-form {
    flex-direction: row;
    align-items: start;
  }
}

.form-field {
  position: relative;
  margin-bottom: 25px;
}
@media screen and (min-width: 768px) {
  .form-field {
    margin: 0 30px 0 0;
  }
}

.input:last-child {
  margin: 0;
}

.input-search {
  display: block;
  width: 270px;
  height: 40px;
  margin-left: auto;
  margin-right: auto;
  padding-left: 30px;
  font-size: 16px;
  line-height: 1.25;
  border-radius: 20px;
  background: var(--bg-input);
  color: var(--blurry-text-color);
  --webkit-backdrop-filter: blur(20px);
  -webkit-backdrop-filter: blur(20px);
  backdrop-filter: blur(20px);
  border: 0;
  outline: none;
  -webkit-appearance: none;
  -moz-appearance: none;
  appearance: none;
}

.input-search:focus {
  outline: 1px solid #fff;
  outline-offset: 3px;
}

.search-btn {
  position: absolute;
  top: 50%;
  right: 20px;
  transform: translateY(-50%);
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 3px;
  border: none;
  border-radius: 50%;
  cursor: pointer;
  background-color: transparent;
}
.search-btn:hover, .search-btn:focus {
  outline-offset: 3px;
}
.search-btn .input-icon {
  display: block;
  width: 12px;
  height: 12px;
  transition: fill 0.1s linear;
}

.close-dropdown {
  position: absolute;
  top: 15px;
  right: 30px;
  z-index: 1000;
  display: flex;
  align-items: center;
  border: none;
  background-color: transparent;
  cursor: pointer;
}
.close-dropdown .input-icon {
  display: block;
  width: 12px;
  height: 12px;
  transition: fill 0.1s linear;
}

.select__dropdown {
  display: none;
  position: absolute;
  padding-left: 0px;
  left: 0;
  right: 0;
  height: 210px;
  overflow: hidden;
  border-radius: 0px 0px 20px 20px;
}
.select.open .select__dropdown {
  display: block;
  position: absolute;
  top: 0;
  left: 0;
  z-index: 900;
  padding: 0;
  border-radius: 20px;
  background: rgba(255, 255, 255, 0.2);
  -webkit-backdrop-filter: blur(20px);
  backdrop-filter: blur(20px);
}
.select__list {
  padding: 0;
  margin: 0;
  list-style: none;
}
.select__item {
  width: 100%;
  display: flex;
  height: 25px;
  padding-left: 30px;
  align-items: center;
  background-color: transparent;
  color: #ccc;
  border: 0;
}
.select__item.selected {
  background-color: var(--accent-text-color);
}
.select__item:hover, .select__item:focus {
  background-color: var(--accent-text-color);
  cursor: pointer;
  transition: 0.15s background-color ease-in;
}

.countries-list {
  height: 100%;
  overflow-y: scroll;
}
.countries-list::-webkit-scrollbar {
  width: 8px;
}
.countries-list::before {
  content: "Choose country";
  display: block;
  width: 80%;
  margin-left: 30px;
  margin-bottom: 5px;
  border-bottom: 1px solid #939393;
  color: #939393;
  padding: 15px 0 10px;
}

.gallery__meta_name,
.gallery__meta_date,
.gallery__meta_place {
  margin-bottom: 0;
}

.gallery__meta_name {
  font-size: 14px;
  font-weight: 700;
  color: var(--accent-text-color);
}
@media screen and (min-width: 768px) {
  .gallery__meta_name {
    font-size: 16px;
  }
}

.gallery__meta_date {
  font-size: 17px;
  font-weight: 400;
  color: var(--light-text-color);
}
@media screen and (min-width: 768px) {
  .gallery__meta_date {
    font-size: 20px;
  }
}

.gallery__meta_place {
  font-size: 15px;
  font-weight: 700;
  color: var(--light-text-color);
  margin-bottom: 5px;
}
@media screen and (min-width: 768px) {
  .gallery__meta_place {
    font-size: 17px;
  }
}

.flex-svg {
  display: flex;
  justify-content: center;
  align-items: center;
}

.gallery__item-card .icon {
  width: 6px;
  height: 9px;
  margin-right: 4px;
  margin-left: 5px;
  fill: var(--light-text-color);
}
@media screen and (min-width: 768px) {
  .gallery__item-card .icon {
    width: 7px;
    height: 10px;
  }
}

.gallery {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  grid-column-gap: 14px;
  grid-row-gap: 14px;
  text-align: center;
  justify-content: center;
  position: relative;
  padding-top: 25px;
}
@media screen and (min-width: 768px) {
  .gallery {
    grid-template-columns: repeat(3, 1fr);
    grid-column-gap: 30px;
    grid-row-gap: 30px;
  }
}
@media screen and (min-width: 1280px) {
  .gallery {
    grid-template-columns: repeat(4, 1fr);
    grid-column-gap: 70px;
    grid-row-gap: 70px;
  }
}

.gallery__item-card {
  position: relative;
  cursor: pointer;
}

.pseudo::after {
  content: "";
  display: block;
  width: 109px;
  height: 101px;
  border: 1px solid rgba(220, 86, 197, 0.3);
  border-radius: 50px 0px;
  position: absolute;
  top: -9px;
  right: -10px;
}
@media screen and (min-width: 768px) {
  .pseudo::after {
    width: 153px;
    height: 143px;
    top: -13px;
    right: 0;
  }
}
@media screen and (min-width: 1280px) {
  .pseudo::after {
    width: 153px;
    height: 143px;
    top: -13px;
    right: -14px;
  }
}

.gallery__image {
  width: 128px;
  height: 161px;
  overflow: hidden;
  margin-right: auto;
  margin-left: auto;
}
@media screen and (min-width: 768px) {
  .gallery__image {
    width: 180px;
    height: 227px;
  }
}

.gallery__img {
  max-width: 100%;
  height: 100%;
  display: block;
  -o-object-fit: cover;
  object-fit: cover;
  border-radius: 50px 0px;
}

.gallery_margin {
  margin-top: 5px;
}

.gallery__item-card {
  transition: transform linear var(--duration);
  border-radius: 50px 0px 25px 0px;
}

.gallery__item-card:hover,
.gallery__item-card:focus {
  transform: scale(1.05);
  box-shadow: 10px 10px 8px 10px rgba(220, 86, 197, 0.1);
}

@-webkit-keyframes fade-in {
  0% {
    opacity: 0;
  }
  100% {
    opacity: 1;
  }
}
@keyframes fade-in {
  0% {
    opacity: 0;
  }
  100% {
    opacity: 1;
  }
}
.gallery {
  -webkit-animation: fade-in 2s linear;
  /* Safari 4+ */
  /* Fx 5+ */
  /* Opera 12+ */
  animation: fade-in 2s linear;
  /* IE 10+, Fx 29+ */
}

.section-pagination {
  padding-top: 40px;
  padding-bottom: 40px;
}

.footer__section-container {
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding-top: 15px;
  padding-bottom: 15px;
  color: var(--blurry-text-color);
}
@media screen and (max-width: 767px) {
  .footer__section-container {
    padding-left: auto;
    padding-right: auto;
  }
}
@media screen and (min-width: 768px) {
  .footer__section-container {
    flex-direction: row;
  }
}

.footer__text {
  display: flex;
  justify-content: center;
  margin: 0;
  font-style: normal;
  font-weight: 400;
  font-size: 14px;
  line-height: 17px;
  background-color: var(--main-text-color);
}
@media screen and (min-width: 768px) {
  .footer__text {
    font-size: 16px;
    line-height: 20px;
  }
}

.heart-icon {
  padding-left: 6px;
  padding-right: 5px;
  padding-bottom: 0;
  font-size: 25px;
  color: #d40000;
  -webkit-animation: animateScale 1700ms infinite cubic-bezier(0.4, 0, 0.2, 1);
  animation: animateScale 1700ms infinite cubic-bezier(0.4, 0, 0.2, 1);
}

.footer__section-container:hover,
.footer__section-container:focus {
  color: var(--light-text-color);
  cursor: pointer;
}
.footer__section-container:hover .heart-icon,
.footer__section-container:focus .heart-icon {
  -webkit-animation: animateScale 500ms infinite cubic-bezier(0.4, 0, 0.2, 1);
  animation: animateScale 500ms infinite cubic-bezier(0.4, 0, 0.2, 1);
}

.developers {
  padding-left: 4px;
}

@-webkit-keyframes animateScale {
  0% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.2);
    color: red;
  }
  100% {
    transform: scale(1);
  }
}
@keyframes animateScale {
  0% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.2);
    color: red;
  }
  100% {
    transform: scale(1);
  }
}
.footer__modal {
  display: none;
}

.footer__modal.is-open {
  display: block;
  position: fixed;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  right: 0;
  background-color: var(--bg-input);
  text-align: center;
  padding-top: 40px;
}
.footer__modal.is-open .footer__modal-container {
  width: 270px;
  height: 400px;
  background-color: var(--dark-bg-color);
  margin-left: calc((100% - 270px) / 2);
  border-radius: 20px;
  padding: 20px;
}
@media screen and (min-width: 768px) {
  .footer__modal.is-open .footer__modal-container {
    width: 700px;
    margin-left: calc((100% - 700px) / 2);
  }
}

.footer__modal-item {
  width: 230px;
  height: 330px;
  background-color: var(--bg-input);
  padding: 10px;
  border-radius: 20px;
  transition: box-shadow ease var(--duration);
}
@media screen and (min-width: 768px) {
  .footer__modal-item {
    width: calc((700px - 80px) / 3);
    margin-right: 20px;
  }
}

.footer__modal-item:hover {
  box-shadow: inset 0 0 20px 15px rgba(220, 86, 197, 0.2);
}

.footer__modal-image {
  margin-left: auto;
  margin-right: auto;
  margin-bottom: 15px;
  width: 180px;
  height: 150px;
  border: 1px solid #fff;
  border-radius: 10px;
  overflow: hidden;
}

.footer__modal-title {
  font-size: 20px;
  line-height: 1.25;
  color: var(--light-text-color);
}

.footer__modal-text {
  font-size: 18px;
  line-height: 1.25;
  color: var(--accent-text-color);
}
.footer__modal-text .icon {
  display: block;
  margin-right: auto;
  margin-left: auto;
  margin-top: 15px;
  width: 30px;
  height: 30px;
  font-size: 25px;
}

.arrow-left,
.arrow-right {
  width: 30px;
  height: 30px;
  margin-top: 10px;
  transition: transform linear var(--duration);
}
.arrow-left .icon,
.arrow-right .icon {
  fill: var(--accent-text-color);
}

.arrow-left {
  margin-right: 10px;
}

.arrow-left:hover,
.arrow-right:hover,
.arrow-left:focus,
.arrow-right:focus {
  transform: scale(1.5);
}

