}

.list {
  margin: 0;
  padding-left: 0;
  list-style-type: none;
}

.img {
  display: block;
  max-width: 100%;
  height: auto;
}

.is-hidden {
  display: none !important;
}

.visually-hidden {
  position: absolute !important;
  clip: rect(1px 1px 1px 1px);
  clip: rect(1px, 1px, 1px, 1px);
  padding: 0 !important;
  border: 0 !important;
  height: 1px !important;
  width: 1px !important;
  overflow: hidden;
}

