  display: none;
  opacity: 0;
  pointer-events: none;
  visibility: none;
  transition: opacity 300ms linear;

  &.is-open {
    position: fixed;
    top: 0;
    left: 0;
    z-index: 1500;

    display: flex;
    width: 100%;
    padding: 50px 0;
    height: 100vh;
    overflow-y: scroll;
    overflow-x: hidden;
    visibility: visible;
    pointer-events: auto;
    opacity: 1;

    background: rgba(32, 13, 29, 0.6);
    -webkit-backdrop-filter: blur(3px);
    backdrop-filter: blur(3px);
    transition: opacity 250ms linear;
  }
}

.modal {
  position: absolute;
  top: 50px;
  left: 50%;
  transform: translateX(-50%);
  border-top-left-radius: 50%;
  border-bottom-right-radius: 50%;
  background-color: #fff2fd;

  @media screen and (max-width: 767.98px) {
    width: 275px;
    padding: 15px;
  }
  @media screen and (min-width: 768px) {
    width: 595px;
    padding: 30px 15px 55px 50px;
    border-radius: 300px 0px;
  }

  @media screen and (min-width: 1280px) {
    width: 930px;
    padding-top: 60px;
    padding-bottom: 60px;
    border-radius: 300px 0px;
  }
}

.modal__btn-close {
  position: absolute;
  top: 30px;
  right: 30px;
  width: 40px;
  height: 40px;
  border: none;
  border-radius: 50%;
  cursor: pointer;
  background-color: transparent;
  transition: background-color 250ms linear, color 250ms linear;

  &:hover,
  &:focus {
    color: #fff;
    background-color: #dc56c5;
  }
}

.event__subtitle {
  margin-bottom: 5px;
  text-transform: uppercase;
  font-weight: 600;
  color: #dc56c5;
  font-size: 20px;
  line-height: 1.2;
}

.event__text {
  margin-bottom: 10px;
  color: #0e0e0e;
}

.event__text--first-string {
  margin-bottom: 3px;
}

.button {
  height: 40px;

  font-weight: 500;
  font-size: 16px;
  line-height: 1.25;
  text-transform: uppercase;

  border-radius: 5px;
}

.event__button {
  margin-bottom: 20px;
  width: 145px;

  background-color: #4c00fe;
  color: #ffffff;
  border: transparent;
}

